var fs = require('fs');
var filename = '/sys/class/gpio/gpio117/value'; // TODO: Change 117 back to 34
var value = new Buffer(1);
var Epoll = require('epoll').Epoll;
var pollpri = new Epoll(function(err, fd, events) {
    fs.readSync(fd, value, 0, 1, 0);
    console.log(value.toString());
});
var valuefd = fs.openSync(filename, 'r');
// Read the GPIO value file before polling to
// prevent an initial unauthentic interrupt.
fs.readSync(valuefd, value, 0, 1, 0);
pollpri.add(valuefd, Epoll.EPOLLPRI);

