var bone = require('./bone');
var my = require('./my');

var m = {};
m.module = my.require('serialport');
m.ports = bone.uarts;
m.openPorts = {};
m.name = 'serialport';
m.openMethod = m.module.SerialPort;
m.events = {
    'open': [],
    'data': ['data']
}

exports.serialOpen = my.wrapOpen(m);
exports.serialWrite = my.wrapCall(m, 'write', ['data'], ['err', 'results']);
